import { uiActions } from "./ui-slice";
import { cartAction } from "./cart-slice";
export const fetchCartData = () => {
   return async (dispatch) => {
        const fetchData = async () => {
            const response = await fetch('https://redux-project-bf2af-default-rtdb.asia-southeast1.firebasedatabase.app/Cart.json', )
            
            if (!response.ok) {
                throw new Error('Could not fetch cart data!')
            }

            const data = await response.json()
            return data
        }

        try {
          const cartData = await fetchData()
          dispatch(cartAction.replaceCart({
            items: cartData.items || [],
            totalQuantity: cartData.totalQuantity,
          }))
        } catch (error) {
            dispatch(uiActions.showNotification({
                status: 'error',
                title: 'Error...',
                message: 'Fetching cart data failed!'
            }))
        }
   }
}

export const sendCartDT = (cart) => {
    return async (dispatch) => {
        dispatch(uiActions.showNotification({
            status: 'pending',
            title: 'Sending...',
            message: 'Sending cart data!'
          }))

        const sendRequest = async () => {
            const response = fetch ('https://redux-project-bf2af-default-rtdb.asia-southeast1.firebasedatabase.app/Cart.json', {
            method: 'PUT',
            body: JSON.stringify({items: cart.items, totalQuantity: cart.totalQuantity})
            })
      
            if (!response.ok) {
                throw new Error('Sending cart data failed')
            } 
        }

       try {
        await sendRequest()
        dispatch(uiActions.showNotification({
            status: 'success',
            title: 'Success...',
            message: 'Sent cart data successfully!'
          }))
         
       } catch (error) {
            dispatch(uiActions.showNotification({
                status: 'error',
                title: 'Error...',
                message: 'Sent cart data failed!'
          }))
       }

        
    }
}
