import React from 'react';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import Notification from './components/UI/Notification';
import { useSelector, useDispatch } from 'react-redux';
import { uiActions } from './store/ui-slice';
import { useEffect } from 'react';

let isInitial = true

function App() {
  const dispatch = useDispatch()
  const showCart = useSelector(state => state.ui.cartIsVisible)
  const cart = useSelector(state => state.cart)
  const notification = useSelector(state => state.ui.notification)

  useEffect(() => {
   const sendCartData = async () => {
    dispatch(uiActions.showNotification({
      status: 'pending',
      title: 'Sending...',
      message: 'Sending cart data!'
    }))
    const response = fetch ('https://redux-project-bf2af-default-rtdb.asia-southeast1.firebasedatabase.app/Cart.json', {
      method: 'PUT',
      body: JSON.stringify(cart)
    })

    if (!response.ok) {
      throw new Error('Sending cart data failed')
    }
    dispatch(uiActions.showNotification({
      status: 'success',
      title: 'Success...',
      message: 'Sent cart data successfully!'
    }))
   }

   if (isInitial) {
    isInitial = false
    return 
   }

   sendCartData().catch(error => {
    dispatch(uiActions.showNotification({
      status: 'error',
      title: 'Error...',
      message: 'Sent cart data failed!'
    }))
   })

  }, [cart, dispatch])

  return (
    <React.Fragment>
      {notification && <Notification status={notification.status} title={notification.title} message={notification.message}></Notification>}
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </React.Fragment>
  );
}

export default App;
