    const formIsValid = enteredCityIsValid && enteredNameIsValid && enteredStreetIsValid && enteredPostalIsValid

    if (!formIsValid) {
        return;
    }
  

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div className={`${classes.control} ${formCheckValid.name ? '' : classes.invalid}`}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' ref={nameInputRef} onChange={enteredNameHandler} onBlur={blurName}/>
        {!formCheckValid.name && <p>Please enter a valid name</p>}
      </div>
      <div className={`${classes.control} ${formCheckValid.street ? '' : classes.invalid}`}>
        <label htmlFor='street'>Street</label>
        <input type='text' id='street' ref={streetInputRef}/>
        {!formCheckValid.street && <p>Please enter a valid street</p>}
      </div>
      <div className={`${classes.control} ${formCheckValid.postalCode ? '' : classes.invalid}`}>
        <label htmlFor='postal'>Postal Code</label>
        <input type='text' id='postal' ref={postalInputRef}/>
        {!formCheckValid.postalCode && <p>Please enter a valid postalCode</p>}
      </div>
      <div className={`${classes.control} ${formCheckValid.city ? '' : classes.invalid}`}>
        <label htmlFor='city'>City</label>
        <input type='text' id='city' ref={cityInputRef}/>
        {!formCheckValid.city && <p>Please enter a valid city</p>}
      </div>
      <div className={classes.actions}>
        <button type='button' onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
